import { createContext, useReducer } from 'react'

export const FilteredStreamersContext = createContext()

export const filteredStreamersReducer = (state, action) => {
    switch (action.type) {
        case 'SET_FSTREAMERS':
            return {
                filteredStreamers: action.payload
            }
        case 'SET_FSTREAMER':
            return {
                filteredStreamers: [action.payload, ...state.filteredStreamers]
            }
        case 'DELETE_FSTREAMER':
            return {
                filteredStreamers: state.filteredStreamers.filter((filteredStreamer) => filteredStreamer._id !== action.payload._id)
            }
        default:
            return state
    }

}

export const FilteredStreamersContextProvider = ({ children }) => {
    const [state, dispatch] = useReducer(filteredStreamersReducer, {
        filteredStreamers: []
    })

    return (
        <FilteredStreamersContext.Provider value={{...state, dispatch}}>
            {children}
        </FilteredStreamersContext.Provider>
    )
}
