import useStreamersContext from "../hooks/StreamersContextHook"

const SortDropdown = ( {streamers} ) => {
    // const {streamers, dispatch} = useStreamersContext()
    
    const options = [
        {
            label: "-",
            value: "-"
        },   
        {
            label: "Most Viewers",
            value: "mostViewers"
        },
        {
            label: "Least Viewers",
            value: "leastViewers"
        },
    ]

    const handleChange = (e) => {
        switch (e.target.value) {
            case "mostViewers":
                return mostViewers()

            case "leastViewers":
                return leastViewers()
            
            default:
                return
        }
    }
    
    const mostViewers = () => {
        const sorted = streamers.sort((a, b) => {
            if (a.viewers > b.viewers) {
                return -1
            }
            if (a.viewers < b.viewers) {
                return 1
            }
            return 0
        })
        streamers = sorted
    }

    // const mostViewers = () => {
    //     const sorted = streamers.sort((a, b) => {
    //         if (a.viewers > b.viewers) {
    //             return -1
    //         }
    //         if (a.viewers < b.viewers) {
    //             return 1
    //         }
    //         return 0
    //     })
    //     dispatch({type: 'SET_STREAMERS', payload: sorted})
    // }

    // const leastViewers = () => {
    //     const sorted = streamers.sort((a, b) => {
    //         if (a.viewers < b.viewers) {
    //             return -1
    //         }
    //         if (a.viewers > b.viewers) {
    //             return 1
    //         }
    //         return 0
    //     })
    //     dispatch({type: 'SET_STREAMERS', payload: sorted})
    // }

    return (
        <div className="sort-dropdown">
            <p>Sort</p>
            <select onChange={handleChange}>
                {options.map((option) => (
                    <option key={option.value} value={option.value}>{option.label}</option>
                ))}
            </select>
        </div>
    )
}

export default SortDropdown
