import { useContext } from 'react'
import { FilteredStreamersContext } from '../contexts/FilteredStreamersContext'

const useFilteredStreamersContext = () => {
    const context = useContext(FilteredStreamersContext)

    if (!context) {
        throw Error('useFilteredStreamersContext must be used within a FilteredStreamersContextProvider')
    }

    return context
}

export default useFilteredStreamersContext
