import useStreamersContext from "../hooks/StreamersContextHook"
import { useState } from "react"

const CustomRangeFilter = ({type, dataset, criterion}) => {
    const {dispatch} = useStreamersContext()
    const [min, setMin] = useState('')
    const [max, setMax] = useState('')

    // filtering should just fetch from database using queries ... same with sort
    // - with a bunch of users they will need to be loaded in batches from db anyways

    const handleSubmit = async (e) => {
        e.preventDefault()
        const filteredStreamers = await fetchRange()
        dispatch({type: "SET_STREAMERS", payload: filteredStreamers})
    }

    const fetchRange = async () => {
        const url = `/api/streamers/range?attribute=${criterion}&min=${min}&max=${max}`

        const response = await fetch(url)

        const json = await response.json()
        
        if (response.ok) {
            return json
        }
    }
    
    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div className="custom-filter-grid">
                    <div>
                        <label>Min</label>
                        <input value={min} onChange={(e) => setMin(e.target.value)} type={type} />
                    </div>
                    <p>-</p>
                    <div>
                        <label>Max</label>
                        <input value={max} onChange={(e) => setMax(e.target.value)} type={type} />
                    </div>
                </div>
                <button>Apply Changes</button>
            </form>
        </div>
    )
}

export default CustomRangeFilter
