import StreamersListEntry from "./StreamersListEntry"
import { useEffect, useState } from "react"
import useStreamersContext from "../hooks/StreamersContextHook"

const StreamersList = () => {
    const {state, dispatch} = useStreamersContext

    useEffect(() => {
        const getStreamers = async () => {
            const response = await fetch('/api/streamers/')
            const json = await response.json()
            // setStreamers(json)
            if (response) {
                dispatch({type:'SET_STREAMERS', json})
            }
        }
        getStreamers()
    }, [])

    return (
        <div>
            {state.streamers && state.streamers.map((streamer) => {
                return <StreamersListEntry key={streamer._id} streamer={streamer}/>
            })}
        </div>
    )
}

export default StreamersList
