import { useDispatch, useSelector } from "react-redux"
import { useEffect } from "react"

// trying to update streamer stack by sending 'upload' from uploadSlice to 'streamersSelected' in uploadSlice


const PurchaseAdsButton = () => {
    const { upload, streamersSelected } = useSelector(state => state.upload)

    const dispatch = useDispatch()

    useEffect(() => {
        const patchStreamers = async () => {
            const response = await fetch('/api/streamers/', {
                headers: {
                    "Content-Type": 'application/json'
                },
                method: 'patch',

            })
            const json = await response.json()
            
            if (response.ok) {
                dispatch(patchStreamers(json))
            }
        }
        patchStreamers()
    }, [dispatch])
    
    const handleClick = () => {
        console.log('click')
    }
    
    return (
        <div>
            {/* <button className="btn">Purchase Ad</button> */}
            <button onClick={handleClick}>Purchase Ad</button>
        </div>
    )
}

export default PurchaseAdsButton
