import {createSlice} from '@reduxjs/toolkit'

export const uploadSlice = createSlice({
    name: 'upload',
    initialState: {
        upload: '',
        streamersSelected: []
    },
    reducers: {
        changeUpload: (state, action) => {
            state.upload = [action.payload]
        },
        addStreamerSelected: (state, action) => {
            state.streamersSelected = [action.payload, ...state.streamersSelected]
        },
        removeStreamer: (state, action) => {
            state.streamersSelected = state.streamersSelected.filter(streamer => (action.payload._id !== streamerSelected._id))
        }
    }
})

export const {changeUpload, addStreamerSelected, removeStreamerSelected} = uploadSlice.actions

export default uploadSlice.reducer
