import useStreamersContext from "../hooks/StreamersContextHook"

const RangeFilters = ({options, criterion}) => {
    const {dispatch} = useStreamersContext()

    const handleChange = async (e) => {
        const {min, max} = options.find(option => option.description === e.target.value)
        // const filteredStreamers = await fetchRange(min, max)
        // dispatch({type: "SET_STREAMERS", payload: filteredStreamers})
        filters.map((filter) => {
            if(filter.criterion === criterion){
                return dispatch(removeFilter(filter))
            }
        })
        dispatch(addFilter({criterion, min, max}))
    }

    // const fetchRange = async (min, max) => {
    //     const url = `/api/streamers/range?attribute=${criterion}&min=${min}&max=${max}`

    //     const response = await fetch(url)

    //     const json = await response.json()
        
    //     if (response.ok) {
    //         return json
    //     }
    // }

    return (
        <div>
            {options.map((option) => (
                <div key={option.description} className="range-filter">
                    <input type="radio" name={criterion} value={option.description} onChange={handleChange} />
                    <label>{option.description}</label>
                </div>
            ))}
        </div>
    )
}

export default RangeFilters
