import useStreamersContext from "../hooks/StreamersContextHook"

const RangeFilters = ({options, criterion}) => {
    const {streamers, dispatch} = useStreamersContext()

    // how do?
    // first set up checkboxes, only one selected at a time

    const handleChange = (e) => {
        const filteredStreamers = filterByCriterion(e.target.id)
        dispatch({type: "SET_STREAMERS", payload: filteredStreamers})
    }

    const filterByCriterion = (range) => {
        return streamers.filter((stream) => (stream[criterion] >= range[0] && stream[criterion] <= range[1]))
    }

    return (
        <div>
            {options.map((option) => (
                <div key={option.description} className="range-filter">
                    <input onChange={handleChange} type="checkbox" id={[option.min, option.max]} />
                    <p>{option.description}</p>
                </div>
            ))}
        </div>
    )
}

export default RangeFilters
