import useStreamersContext from "../hooks/StreamersContextHook"
import { useState } from "react"

const CustomRangeFilter = ({dataset, criterion}) => {
    const {dispatch} = useStreamersContext()
    const [min, setMin] = useState('')
    const [max, setMax] = useState('')


    const handleSubmit = (e) => {
        e.preventDefault()
        // filter()
    }

    // const filter = () => {
    //     const streamers = dataset.filter((data) => {
    //         if (data >= min && data <= max) {
    //             return data
    //         }
    //     })
    //     return dispatch({type: "SET_STREAMERS", payload: streamers})
    // }

    
    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div className="custom-filter-grid">
                    <div>
                        <label>Min</label>
                        <input value={min} onChange={(e) => setMin(e.target.value)} type="text" />
                    </div>
                    <p>-</p>
                    <div>
                        <label>Max</label>
                        <input value={max} onChange={(e) => setMax(e.target.value)} type="text" />
                    </div>
                </div>
                <button>Apply Changes</button>
            </form>
        </div>
    )
}

export default CustomRangeFilter
