import {createSlice} from '@reduxjs/toolkit'

export const uploadSlice = createSlice({
    name: 'upload',
    initialState: {
        upload: '',
        streamers: []
    },
    reducers: {
        changeUpload: (state, action) => {
            state.upload = [action.payload]
        },
        addStreamer: (state, action) => {
            state.streamers = [action.payload, ...state.streamers]
            console.log(state.streamers)
        },
        removeStreamer: (state, action) => {
            state.streamers = state.streamers.filter(streamer => (action.payload._id !== streamer._id))
            console.log(state.streamers)
        }
    }
})

export const {changeUpload, addStreamer, removeStreamer} = uploadSlice.actions

export default uploadSlice.reducer
