import FiltersSidebar from "./FiltersSidebar"
import SortDropdown from "./SortDropdown"
import StreamersList from "./StreamersList"
import PreviewAdButton from "./PreviewAdButton"
import PurchaseAdsButton from "./PurchaseAdsButton"
import useStreamersContext from "../hooks/StreamersContextHook"
import {useEffect} from 'react'

const StreamersLiveUI = () => {
    const {streamers, dispatch} = useStreamersContext()
    useEffect(() => {
        const getStreamers = async () => {
            const response = await fetch('/api/streamers/')
            const json = await response.json()
            
            if (response.ok) {
                dispatch({
                    type:"SET_STREAMERS", 
                    payload: json
                })
            }
        }
        getStreamers()
    }, [dispatch])

    let filteredStreamers = streamers

    return (
        <div className="streamers-live">
            <div className="info-grid">
                <FiltersSidebar />
                <div>
                    <div className="heading-grid">
                        <h1>Streamers Live</h1>
                        <SortDropdown />
                    </div>
                    <StreamersList streamers={filteredStreamers} />
                </div>
            </div>
            <div className="buttons">
                <PreviewAdButton />
                <PurchaseAdsButton />
            </div>
        </div>
    )
}

export default StreamersLiveUI
