import StreamersListEntry from "./StreamersListEntry"
import { useSelector } from "react-redux"

const StreamersList = () => {
    const { streamers } = useSelector(state => state.streamer)
    const { sorting } = useSelector(state => state.filter)
    // const sortedStreamers = streamers
    if (sorting.criterion) {
    //     // Error in this section, soting criterion correct...
        const sortedStreamers = streamers.sort((a, b) => {
            if (a.viewers < b.viewers) {
                return -1
            }
            if (a.viewers > b.viewers) {
                return 1
            }
            return 0
        })
        console.log(streamers)
    }


    return (
        <div>
            {streamers && streamers.map((streamer) => {
                return <StreamersListEntry key={streamer._id} streamer={streamer}/>
            })}
        </div>
    )
}

export default StreamersList
