let open = true

const handleClick = () => {
    open = !open
    console.log(open)
}

const CollapseFilter = ({title, component}) => {

    return (
        <div>
            <div className="collapse-header">
                <h2>{title}</h2>
                <button onClick={handleClick}>A</button>
            </div>
            {this.open && component}
        </div>
    )
}

export default CollapseFilter
