import CollapseFilter from "./CollapseFilter"
import CustomRangeFilter from "./CustomRangeFilter"
import RangeFilters from "./RangeFilters"
import useStreamersContext from "../hooks/StreamersContextHook"

const FiltersSidebar = () => {
    const {streamers} = useStreamersContext()
    
    const viewCountOptions = ["Less than 10", "10 to 40", "40 to 100", "More than 100"]
    const followersOptions = ["Less than 10", "10 to 40", "40 to 100", "More than 100"]
    const adsQueuedOptions = ["Less than 10", "10 to 40", "40 to 100", "More than 100"]

    
    return (
        <div className="filter-sidebar">
            <CollapseFilter title="View Count" component={<><CustomRangeFilter type={"number"} dataset={streamers} criterion={"viewers"} /><RangeFilters options={viewCountOptions} /></>} />
            <CollapseFilter title="Followers" component={<><CustomRangeFilter type={"number"} dataset={streamers} criterion={"followers"} /><RangeFilters options={followersOptions} /></>} />
            {/* adsQueued does not reference the stack length, convert to query then adress problem */}
            <CollapseFilter title="Ads Queued" component={<><CustomRangeFilter type={"number"} dataset={streamers} criterion={"adsQueued"}/><RangeFilters options={adsQueuedOptions} /></>} />
        </div>
    )
}

export default FiltersSidebar
