import useStreamersContext from "../hooks/StreamersContextHook"

const RangeFilters = ({options, criterion}) => {
    const {streamers, dispatch} = useStreamersContext()

    // set up checkboxes, only one selected at a time

    const handleChange = (e) => {
        console.log(e.target.value)
        const {min, max} = options.find(option => option.description === e.target.value)
        const filteredStreamers = filterByCriterion(min, max)
        dispatch({type: "SET_STREAMERS", payload: filteredStreamers})
    }

    const filterByCriterion = (min, max) => {
        return streamers.filter((stream) => (stream[criterion] >= min && stream[criterion] <= max))
    }

    return (
        <div>
            {/* {options.map((option) => (
                <div key={option.description} className="range-filter">
                    <input onChange={handleChange} type="checkbox" id={[option.description]} />
                    <p>{option.description}</p>
                </div>
            ))} */}
            {options.map((option) => (
                <div key={option.description} className="range-filter">
                    <input type="radio" name={criterion} value={option.description} onChange={handleChange} />
                    <label>{option.description}</label>
                </div>
            ))}
        </div>
    )
}

export default RangeFilters
